// map screen
function MapScreen({ navigation }) {
  // declare map variables
  const [search, changeSearch] = useState("");
  const [lat, changeLat] = useState(46.54489);
  const [lon, changeLon] = useState(14.95645);
  let cityName;

  useEffect(() => {
    // on home screen focus fetch data from api
    const unsubscribe = navigation.addListener("focus", () => {
      fetch(ip)
        .then((response) => response.json())
        .then((json) => {
          // set variables to response data
          changeLat(json.coord.lat);
          changeLon(json.coord.lon);
        })
        .catch((error) => {
          console.error(error);
        });
    });
    return unsubscribe;
  }, [navigation]);

  return (
    <View style={{ flex: 1, marginTop: 30, padding: 10 }}>
      <SearchBar
        placeholder="City.."
        platform="android"
        onChangeText={changeSearch}
        value={search}
        containerStyle={{ paddingBottom: 0, paddingTop: 0 }}
      />
      <Button
        title="Apply"
        onPress={() => {
          if (search != "") {
            cityName = search;
            fetch(ip + "city/" + cityName)
              .then((response) => response.json())
              .then((json) => {
                Alert.alert(
                  "Change city",
                  'You changed city to "' + cityName + '" ?',
                  [
                    {
                      text: "OK",
                      onPress: () => {
                        if (json.cod == 200) {
                          changeLat(json.coord.lat);
                          changeLon(json.coord.lon);
                          navigation.navigate("Home");
                        } else {
                          Alert.alert(
                            "Invalid name",
                            "You inserted invalid name",
                            [
                              {
                                text: "OK",
                              },
                            ],
                            { cancelable: false }
                          );
                        }
                      },
                    },
                  ],
                  { cancelable: false }
                );
              });
          }
        }}
      />
      <MapView
        style={{ position: "relative", flex: 1, marginTop: 10 }}
        initialRegion={{
          latitude: lat,
          longitude: lon,
          latitudeDelta: 20,
          longitudeDelta: 20,
        }}
        onPress={(e) => {
          changeLat(e.nativeEvent.coordinate.latitude);
          changeLon(e.nativeEvent.coordinate.longitude);
          fetch(
            ip +
              "coord/lat=" +
              e.nativeEvent.coordinate.latitude +
              "&lon=" +
              e.nativeEvent.coordinate.longitude
          )
            .then((response) => response.json())
            .then((json) => {
              Alert.alert(
                "Change city",
                'You changed city to "' + json.name + '" ?',
                [
                  {
                    text: "OK",
                    onPress: () => {
                      if (json.cod == 200) {
                        navigation.navigate("Home");
                      }
                    },
                  },
                ],
                { cancelable: false }
              );
            });
        }}
      >
        <Marker coordinate={{ latitude: lat, longitude: lon }} />
      </MapView>
    </View>
  );
}

// camera screen
function CameraScreen() {
  return (
    <View
      style={{ flex: 1, justifyContent: "center", alignItems: "center" }}
    ></View>
  );
}

const Tab = createBottomTabNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Tab.Navigator
        screenOptions={({ route }) => ({
          tabBarIcon: ({ focused, color, size }) => {
            let iconName;

            if (route.name === "Home") {
              iconName = focused ? "ios-home" : "ios-home-outline";
            } else if (route.name === "Map") {
              iconName = focused ? "ios-locate" : "ios-locate-outline";
            } else if (route.name === "Camera") {
              iconName = focused ? "ios-camera" : "ios-camera-outline";
            }

            // You can return any component that you like here!
            return <Ionicons name={iconName} size={size} color={color} />;
          },
        })}
        tabBarOptions={{
          activeTintColor: "royalblue",
          inactiveTintColor: "gray",
        }}
      >
        <Tab.Screen name="Home" component={HomeScreen} style={styles.navLink} />
        <Tab.Screen name="Map" component={MapScreen} />
        <Tab.Screen name="Camera" component={CameraScreen} />
      </Tab.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    paddingHorizontal: 10,
  },
  button: {
    alignItems: "center",
    backgroundColor: "#DDDDDD",
    padding: 10,
